import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Football() {
  const [question, setQuestion] = useState(null);
  const [quizComplete, setQuizComplete] = useState(false);
  const [score, setScore] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [timeLeft, setTimeLeft] = useState(10);
  const [questionCount, setQuestionCount] = useState(0);
  const [answered, setAnswered] = useState(false); // Pour vérifier si la question a été répondue

  useEffect(() => {
    fetchFootballQuestion();
  }, []);

  useEffect(() => {
    let timer;
    if (timeLeft > 0 && !answered && !quizComplete) {
      timer = setTimeout(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
    } else if (timeLeft === 0 && !answered) { // Si le temps est écoulé et que l'utilisateur n'a pas répondu
      handleAnswerButtonClick(-1); // Réduire le score d'un point
    }
    return () => clearTimeout(timer);
  }, [timeLeft, answered, quizComplete]);

  const fetchFootballQuestion = async () => {
    try {
      const response = await axios.get('http://localhost:8000/questions/football/random');
      setQuestion(response.data);
      setTimeLeft(10); // Réinitialiser le temps pour chaque nouvelle question
      setAnswered(false); // Réinitialiser l'état de réponse
    } catch (error) {
      console.error('Erreur lors de la récupération de la question de football :', error);
    }
  };

  const handleAnswerButtonClick = (selectedOptionIndex) => {
    clearTimeout(answerTimeout); // Annuler le délai d'attente si l'utilisateur répond avant la fin du temps imparti
    const isCorrect = selectedOptionIndex === question.correctOptionIndex;
    let updatedScore = score;
    if (isCorrect) {
      updatedScore += 5 + timeLeft; // Ajouter le temps restant en points si la réponse est correcte
      setCorrectAnswers(correctAnswers + 1);
    } else if (selectedOptionIndex !== -1) { // Ne réduire le score que si l'utilisateur a répondu
      updatedScore -= 1;
    }
    setScore(updatedScore);
    setAnswered(true); // Marquer la question comme répondue

    setTimeout(() => {
      if (questionCount < 4) { // Arrêter le quiz après 5 questions
        fetchFootballQuestion(); // Charger une nouvelle question après avoir répondu
        setQuestionCount(questionCount + 1);
      } else {
        console.log('Score final :', score);
        setQuizComplete(true); // Marquer le quiz comme terminé après la dernière question
      }
    }, 2000); // Délai avant de passer à la prochaine question
  };

  let answerTimeout;
  useEffect(() => {
    answerTimeout = setTimeout(() => {
      handleAnswerButtonClick(-1); // Réduire le score si l'utilisateur n'a pas répondu à temps
    }, 10000); // 10 secondes
  }, [timeLeft]);

  if (!question) {
    return <div>Loading...</div>;
  }

  return (
    <div className="quiz">
      <div className="quiz-content">
        <div className="question-section tertiary-bg">
          <div className="question-count white">
            Question
          </div>
          <div className="question-text">
            <h2 className="question">{question.question}</h2>
          </div>
        </div>
        <div className="answer-section">
          {question.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleAnswerButtonClick(index)}
              className={quizComplete && index === question.correctOptionIndex ? 'correct' : quizComplete && index !== question.correctOptionIndex ? 'incorrect' : ''}
              disabled={quizComplete}
            >
              {option}
            </button>
          ))}
        </div>
        <div className="time-left">
          Temps restant : {timeLeft} secondes
        </div>
        {quizComplete && questionCount === 5 && (
          <h2>Score final : {score}</h2>
        )}
      </div>
    </div>
  );
}

export default Football;
