import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';
import "./style.css";

export default function Login() {
  const navigate = useNavigate();
  const [data, setData] = useState({
    email: '',
    password: '',
  });

  // useEffect pour vérifier si l'utilisateur est déjà connecté au chargement du composant
  useEffect(() => {
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      // Rediriger vers le tableau de bord si l'utilisateur est déjà connecté
      navigate('/dashboard');
    }
  }, []); // Le tableau vide [] assure que useEffect s'exécute uniquement une fois après le premier rendu

  const loginUser = async (e) => {
    e.preventDefault();
    const { email, password } = data;
    try {
      const response = await axios.post('/login', {
        email,
        password
      });
      const userData = response.data;

      if (userData.error) {
        toast.error(userData.error);
      } else {
        // Enregistrement des informations de connexion dans le localStorage
        localStorage.setItem('user', JSON.stringify(userData));
        setData({ email: '', password: '' });
        navigate('/dashboard');
      }

    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div>
      <form className="formulaire" onSubmit={loginUser}>
        <label>Email</label>
        <input type='email' placeholder='enter email...' value={data.email} onChange={(e) => setData({ ...data, email: e.target.value })} />
        <label>Password</label>
        <input type='password' placeholder='enter password...' value={data.password} onChange={(e) => setData({ ...data, password: e.target.value })} />
        <button className='register' type='submit'>Login</button>
      </form>
    </div>
  );
}
