import React, { useState, useEffect } from 'react';

function Football() {
  const questions = [
    // Vos questions...
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(() => {
    // Chargement du score depuis le stockage local au chargement initial de la page
    const savedScore = localStorage.getItem('footballScore');
    return savedScore ? parseInt(savedScore, 10) : 0;
  });
  const [showScore, setShowScore] = useState(false);

  useEffect(() => {
    // Sauvegarde du score dans le stockage local à chaque changement
    localStorage.setItem('footballScore', score.toString());
  }, [score]);

  const handleAnswerButtonClick = (selectedOption) => {
    if (selectedOption === questions[currentQuestion].answer) {
      setScore(score + 10); // Ajoute 10 points pour une bonne réponse
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  const currentQuestionData = currentQuestion < questions.length ? questions[currentQuestion] : null;

  return (
    <div className="quiz">
      <div className="score-section">
        Score: {score} / {questions.length * 10} {/* Affichage du score total possible */}
      </div>
      {showScore ? (
        <div className="score-section">
          <p>Score final: {score}</p>
        </div>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{currentQuestionData ? currentQuestionData.question : ''}</div>
          </div>
          <div className="answer-section">
            {currentQuestionData && currentQuestionData.options.map((option) => (
              <button key={option} onClick={() => handleAnswerButtonClick(option)}>{option}</button>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default Football;
