import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom'; // Importez Navigate de react-router-dom
import LoginTest from './components/loginTest';
import Register from './components/Register';
import Home from './components/Home';
import Quiz from './components/Quiz';
import Sports from './components/Categories/Sports'; // Importer le composant Sports
import Football from './components/Categories/Sports/Football';
import './App.css';

import logo from './logo.png';

// Fonction qui vérifie si l'utilisateur est authentifié
const isAuthenticated = () => {
  // Implémentez votre logique d'authentification ici
  // Par exemple, vérifiez si l'utilisateur est connecté ou non
  // Retournez true si l'utilisateur est authentifié, false sinon
};

// Composant de route protégée pour les pages de quiz
const ProtectedRoute = ({ element: Element, ...rest }) => (
  <Route
    {...rest}
    element={isAuthenticated() ? <Element /> : <Navigate to="/login" />}
  />
);

function App() {
  return (
    <Router>
      <div className='font-varela bg-white text-black h-screen flex flex-col'>
        <header className="bg-primary py-4 px-6 flex justify-between items-center">
          <div className="flex items-center">
            <img src={logo} alt="Logo" className="h-8 mr-4" />
          </div>
          <div className="flex justify-center flex-grow">
            <ul className="nav-bar flex gap-4 list-none">
              <li className="text-center">
                <Link to="/" className="text-accent font-bold text-xl hover:text-tertiary no-underline">Home</Link>
              </li>
              <li className="text-center">
                <Link to="/quiz" className="text-accent font-bold text-xl hover:text-tertiary no-underline">Quiz</Link>
              </li>
            </ul>
          </div>
          <div className="flex items-center">
            <ul className="nav-bar flex gap-4 list-none">
              <li>
                <Link to="/login" className="text-primary font-bold text-xl rounded-full bg-white px-6 py-3 hover:bg-tertiary transition duration-200 inline-block">
                  Login
                </Link>
              </li>
              <li>
                <Link to="/register" className="text-accent font-bold text-xl rounded-full bg-secondary px-6 py-3 hover:bg-tertiary transition duration-200 inline-block">
                  Register
                </Link>
              </li>
            </ul>
          </div>
        </header>
        <main className="flex-grow flex justify-center">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginTest />} />
            <Route path="/register" element={<Register />} />
            <ProtectedRoute path="/quiz" element={<Quiz />} />
            <Route path="/categories/sports" element={<Sports />} /> {/* Utiliser le composant Sports */}
            <Route path="/categories/football" element={<Football />} /> {/* Utiliser le composant Football */}
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
