import React, { useState, useEffect } from 'react';

function Football() {
  const initialQuestions = [
    {
      id: 1,
      question: "Quelle équipe a remporté le premier championnat du monde de football en 1930 ?",
      options: ["Uruguay", "Brésil", "Allemagne", "Italie"],
      answer: "Uruguay"
    },
    {
      id: 2,
      question: "Dans quelle ville allemande s'est déroulée la finale de la Coupe du Monde 2006 ?",
      options: ["Berlin", "Munich", "Hambourg", "Dortmund"],
      answer: "Berlin"
    },
    {
      id: 3,
      question: "Quelle équipe a remporté le Championnat d'Europe de football en 2016 ?",
      options: ["Portugal", "France", "Espagne", "Allemagne"],
      answer: "Portugal"
    },
    {
      id: 4,
      question: "Qui a marqué le plus de buts en Coupe du Monde de football de tous les temps ?",
      options: ["Pelé", "Cristiano Ronaldo", "Miroslav Klose", "Diego Maradona"],
      answer: "Miroslav Klose"
    },
    {
      id: 5,
      question: "Quel joueur de football argentin est souvent comparé à Diego Maradona ?",
      options: ["Lionel Messi", "Sergio Agüero", "Gonzalo Higuaín", "Ángel Di María"],
      answer: "Lionel Messi"
    },
    {
      id: 6,
      question: "Quelle équipe de football détient le record de victoires en Ligue des champions de l'UEFA ?",
      options: ["Real Madrid", "FC Barcelone", "Bayern Munich", "Liverpool"],
      answer: "Real Madrid"
    },
    {
      id: 7,
      question: "Quel est le surnom de l'équipe nationale de football du Brésil ?",
      options: ["La Seleção", "Les Samba Boys", "Les Auriverdes", "Les Canarinhos"],
      answer: "La Seleção"
    },
    {
      id: 8,
      question: "Quel joueur de football a remporté le Ballon d'Or le plus de fois ?",
      options: ["Lionel Messi", "Cristiano Ronaldo", "Pelé", "Michel Platini"],
      answer: "Lionel Messi"
    },
    {
      id: 9,
      question: "Quel est le plus grand stade de football du monde en capacité ?",
      options: ["Camp Nou", "Stade de Wembley", "Stade Rose Bowl", "Stade Maracanã"],
      answer: "Stade Maracanã"
    },
    {
      id: 10,
      question: "Dans quelle ville italienne se trouve le stade San Siro ?",
      options: ["Milan", "Rome", "Turin", "Naples"],
      answer: "Milan"
    },
    // Ajoutez les questions supplémentaires ici
    {
      id: 11,
      question: "Quelle équipe a remporté le premier championnat d'Europe de football en 1960 ?",
      options: ["Union soviétique", "Yougoslavie", "Tchécoslovaquie", "France"],
      answer: "Union soviétique"
    },
    {
      id: 12,
      question: "Qui est le meilleur buteur de l'histoire de la Bundesliga ?",
      options: ["Robert Lewandowski", "Gerd Müller", "Karl-Heinz Rummenigge", "Jürgen Klinsmann"],
      answer: "Gerd Müller"
    },
    {
      id: 13,
      question: "Quelle équipe de football a remporté le plus de titres de Premier League anglaise ?",
      options: ["Manchester United", "Liverpool", "Arsenal", "Chelsea"],
      answer: "Manchester United"
    },
    {
      id: 14,
      question: "Quelle nation a remporté la Coupe du Monde de la FIFA pour la première fois en 1958 ?",
      options: ["Brésil", "Allemagne", "Italie", "Argentine"],
      answer: "Brésil"
    },
    {
      id: 15,
      question: "Qui a été le premier footballeur à remporter le Ballon d'Or en 1956 ?",
      options: ["Stanley Matthews", "Alfredo Di Stefano", "Ferenc Puskás", "Raymond Kopa"],
      answer: "Stanley Matthews"
    },
    {
      id: 16,
      question: "Quel joueur a été surnommé 'Le Roi Pelé' ?",
      options: ["Pelé", "Diego Maradona", "Lionel Messi", "Cristiano Ronaldo"],
      answer: "Pelé"
    },
    {
      id: 17,
      question: "Quelle équipe a remporté le plus de titres de Serie A italienne ?",
      options: ["Juventus", "AC Milan", "Inter Milan", "AS Roma"],
      answer: "Juventus"
    },
    {
      id: 18,
      question: "Qui a marqué le but connu sous le nom de 'La Main de Dieu' lors de la Coupe du Monde 1986 ?",
      options: ["Diego Maradona", "Pelé", "Michel Platini", "Zinedine Zidane"],
      answer: "Diego Maradona"
    },
    {
      id: 19,
      question: "Quel pays a remporté la Coupe d'Europe des Nations en 2004 ?",
      options: ["Grèce", "Portugal", "Pays-Bas", "Espagne"],
      answer: "Grèce"
    },
    {
      id: 20,
      question: "Quel est le stade de football le plus haut en altitude dans le monde ?",
      options: ["Estadio Hernando Siles", "Stade de Wembley", "Camp Nou", "Stade Maracanã"],
      answer: "Estadio Hernando Siles"
    },
  ];const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [quizComplete, setQuizComplete] = useState(false);
  const [score, setScore] = useState(() => {
    const storedScore = localStorage.getItem('quizScore');
    return storedScore ? parseInt(storedScore) : 0;
  });
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [timeLeft, setTimeLeft] = useState(10);

  useEffect(() => {
    const shuffledQuestions = shuffleArray(initialQuestions).slice(0, 5).map(question => ({
      ...question,
      options: shuffleArray(question.options)
    }));
    setQuestions(shuffledQuestions);

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(prevTimeLeft => {
        if (prevTimeLeft > 0) {
          return prevTimeLeft - 1;
        } else {
          handleAnswerButtonClick(''); // Si le temps est écoulé, passer à la prochaine question
          return 10; // Réinitialise le temps pour la prochaine question
        }
      });
    }, 1000);

    return () => clearInterval(timer);
  }, [currentQuestion]); // On démarre le timer à chaque changement de question

  useEffect(() => {
    localStorage.setItem('quizScore', score.toString());
  }, [score]);

  const shuffleArray = (array) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  const handleAnswerButtonClick = (selectedOption) => {
    const updatedQuestions = questions.map((question, index) => {
      if (index === currentQuestion) {
        const isCorrect = selectedOption === question.answer;
        if (isCorrect) {
          setCorrectAnswers(correctAnswers + 1);
          setScore(prevScore => prevScore + timeLeft); // Augmenter le score en fonction du temps restant si la réponse est correcte
        }
        return { ...question, selectedOption, isCorrect };
      } else {
        return question;
      }
    });

    setQuestions(updatedQuestions);

    setTimeout(() => {
      const nextQuestion = currentQuestion + 1;
      if (nextQuestion < questions.length) {
        setCurrentQuestion(nextQuestion);
        setTimeLeft(10); // Réinitialiser le temps pour la prochaine question
      } else {
        setQuizComplete(true);
      }
    }, 1000);
  };

  const currentQuestionData = currentQuestion < questions.length ? questions[currentQuestion] : null;

  if (quizComplete) {
    const totalQuestions = questions.length;
    const accuracy = (correctAnswers / totalQuestions) * 100;
    return (
      <div className="quiz">
        <h1>Quiz complet</h1>
        <h2>Votre score final est : {score}</h2>
        <h2>Nombre de bonnes réponses : {correctAnswers} / {totalQuestions}</h2>
        <h2>Précision : {accuracy.toFixed(2)}%</h2>
      </div>
    );
  }

  return (
    <div className="quiz">
      <div className="quiz-content">
        <div className="question-section tertiary-bg">
          <div className="question-count white">
            Question {currentQuestion + 1}
          </div>
          <div className="question-text">
            <h2 className="question">{currentQuestionData ? currentQuestionData.question : ''}</h2>
          </div>
        </div>
        <div className="answer-section">
          {currentQuestionData && currentQuestionData.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleAnswerButtonClick(option)}
              className={currentQuestionData.selectedOption && (option === currentQuestionData.answer ? 'correct' : 'incorrect')}
            >
              {option}
            </button>
          ))}
        </div>
        <div className="time-left">
          Temps restant : {timeLeft} secondes
        </div>
      </div>
    </div>
  );
}

export default Football;
