import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, NavLink } from 'react-router-dom';
import LoginTest from './components/loginTest';
import Register from './components/Register';
import Home from './components/Home';
import Quiz from './components/Quiz';
import Sports from './components/Categories/Sports';
import Football from './components/Categories/Sports/Football';
import Dashboard from './components/Dashboard';
import './App.css';
import logo from './logo.png';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Vérifiez s'il y a une session enregistrée dans le localStorage
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setIsLoggedIn(true);
    }
  }, []);

  useEffect(() => {
    // Recharge la page lors du changement de l'état d'authentification
    window.location.reload();
  }, [isLoggedIn]);

  const handleLogout = () => {
    // Déconnectez l'utilisateur et supprimez ses informations de connexion du localStorage
    setIsLoggedIn(false);
    localStorage.removeItem('user');
  };

  let navContent;
  if (isLoggedIn) {
    navContent = (
      <ul className="nav-bar flex gap-4 list-none">
        <li>
          <Link to="/login" onClick={handleLogout} className="text-primary font-bold text-xl rounded-full bg-white px-6 py-3 hover:bg-tertiary transition duration-200 inline-block">
            Mon Compte
          </Link>
        </li>
      </ul>
    );
  } else {
    navContent = (
      <ul className="nav-bar flex gap-4 list-none">
        <li>
          <Link to="/login" className="text-primary font-bold text-xl rounded-full bg-white px-6 py-3 hover:bg-tertiary transition duration-200 inline-block">
            Login
          </Link>
        </li>
        <li>
          <Link to="/register" className="text-accent font-bold text-xl rounded-full bg-secondary px-6 py-3 hover:bg-tertiary transition duration-200 inline-block">
            Register
          </Link>
        </li>
      </ul>
    );
  }

  return (
    <Router>
      <div className='font-varela bg-white text-black h-screen flex flex-col'>
        <header className="bg-primary py-4 px-6 flex justify-between items-center">
          <div className="flex items-center">
            <img src={logo} alt="Logo" className="h-8 mr-4" />
          </div>
          <div className="flex justify-center flex-grow">
            <ul className="nav-bar flex gap-4 list-none">
              <li className="text-center">
                <NavLink to="/" className="text-accent font-bold text-xl hover:text-tertiary no-underline" activeClassName="text-white">Home</NavLink>
              </li>
              <li className="text-center">
                <NavLink to="/quiz" className="text-accent font-bold text-xl hover:text-tertiary no-underline" activeClassName="text-white">Quiz</NavLink>
              </li>
            </ul>
          </div>
          <div className="flex items-center">
            {navContent}
          </div>
        </header>
        <main className="flex-grow flex justify-center">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginTest />} />
            <Route path="/register" element={<Register />} />
            <Route path="/quiz" element={<Quiz />} />
            <Route path="/categories/sports" element={<Sports />} />
            <Route path="/categories/football" element={<Football />} />
            <Route path="/dashboard" element={<Dashboard />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
