import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import LoginTest from './components/loginTest';
import './App.css';

function App() {
  return (
    <Router>
      <div className='font-varela bg-white text-black h-screen flex flex-col'>
        <header className="bg-primary py-4 px-6 flex justify-end items-center">
          <div className="flex items-center h-full"> {/* Utilisez flex et items-center pour aligner verticalement les éléments */}
            <ul className="nav-bar flex gap-4 list-none h-full"> {/* Assurez-vous que la hauteur de la liste correspond à la hauteur de l'en-tête */}
              <li>
                <Link to="/" className="text-accent font-bold text-xl hover:text-tertiary no-underline">Home</Link> 
              </li>
              <li className="flex items-center"> {/* Assurez-vous que le bouton est aligné verticalement */}
                <button className="text-accent font-bold text-xl rounded-full bg-secondary px-6 py-3 hover:bg-tertiary transition duration-200">Login</button>
              </li> 
            </ul>
          </div>
        </header>
        <main className="flex-grow">
          <Routes>
            <Route path="/" element={<div><h1>Bienvenue</h1></div>} />
            <Route path="/login" element={<LoginTest />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
