import React, { useContext } from 'react';
import { UserContext } from '../context/userContext'; // Importez le contexte utilisateur

function Dashboard(props) {
  // Accédez au contexte utilisateur
  const { user } = useContext(UserContext);

  // Utilisez le nom d'utilisateur du contexte
  return (
    <div>
      <h1>{user.name ? `Bienvenue ${user.name} !` : 'Bienvenue !'}</h1>
      <p>Ceci est le tableau de bord.</p>
    </div>
  );
}

export default Dashboard;
