import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const QuestionForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    question: '',
    optionA: '',
    optionB: '',
    optionC: '',
    optionD: '',
    category: '',
    subCategory: '',
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/questions', formData);
      if (response.data.error) {
        setErrorMessage(response.data.error);
        setTimeout(() => {
          setErrorMessage('');
        }, 2000);
      } else {
        setSuccessMessage('Question ajoutée avec succès !');
        setTimeout(() => {
          setSuccessMessage('');
          setFormData({
            question: '',
            optionA: '',
            optionB: '',
            optionC: '',
            optionD: '',
            category: '',
            subCategory: '',
          });
        }, 2000);
      }
    } catch (error) {
      console.error('Erreur lors de l\'ajout de la question :', error);
      setErrorMessage('Erreur lors de l\'ajout de la question');
      setTimeout(() => {
        setErrorMessage('');
      }, 2000);
    }
  };

  const handleOptionChange = (option, value) => {
    setFormData(prevState => ({
      ...prevState,
      [option]: value
    }));
  };

  return (
    <div className="question-form-container">
      <div className="bg-white py-6 sm:py-8 lg:py-12 font-varela">
        <div className="mx-auto max-w-screen-2xl px-4 md:px-8">
          <h2 className="mb-4 text-center text-2xl font-bold text-gray-800 md:mb-8 lg:text-3xl">Ajouter une question</h2>

          <form className="mx-auto max-w-lg rounded-lg border" onSubmit={handleSubmit}>
            <div className="flex flex-col gap-4 p-4 md:p-8 bg-accent">
              <div className="w-full">
                <label htmlFor="question" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Question</label>
                <input
                  type="text"
                  id="question"
                  value={formData.question}
                  onChange={(e) => setFormData({ ...formData, question: e.target.value })}
                  className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                  placeholder="Entrez la question..."
                  required
                />
              </div>

              <div className="flex flex-row gap-4">
                <div className="w-1/2">
                  <label htmlFor="optionA" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Option A</label>
                  <input
                    type="text"
                    id="optionA"
                    value={formData.optionA}
                    onChange={(e) => handleOptionChange('optionA', e.target.value)}
                    className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                    placeholder="Entrez l'option A..."
                    required
                  />
                </div>

                <div className="w-1/2">
                  <label htmlFor="optionB" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Option B</label>
                  <input
                    type="text"
                    id="optionB"
                    value={formData.optionB}
                    onChange={(e) => handleOptionChange('optionB', e.target.value)}
                    className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                    placeholder="Entrez l'option B..."
                    required
                  />
                </div>
              </div>

              <div className="flex flex-row gap-4">
                <div className="w-1/2">
                  <label htmlFor="optionC" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Option C</label>
                  <input
                    type="text"
                    id="optionC"
                    value={formData.optionC}
                    onChange={(e) => handleOptionChange('optionC', e.target.value)}
                    className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                    placeholder="Entrez l'option C..."
                    required
                  />
                </div>

                <div className="w-1/2">
                  <label htmlFor="optionD" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Option D</label>
                  <input
                    type="text"
                    id="optionD"
                    value={formData.optionD}
                    onChange={(e) => handleOptionChange('optionD', e.target.value)}
                    className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                    placeholder="Entrez l'option D..."
                    required
                  />
                </div>
              </div>

              <div className="flex flex-row gap-4">
                <div className="w-full md:w-1/2">
                  <label htmlFor="category" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Catégorie</label>
                  <select
                    id="category"
                    value={formData.category}
                    onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                    className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                    required
                  >
                    <option value="">Sélectionner une catégorie...</option>
                    <option value="Sports">Sports</option>
                    <option value="Cinéma">Cinéma</option>
                    <option value="Histoire">Histoire</option>
                    <option value="Sciences">Sciences</option>
                    <option value="Arts">Arts</option>
                    <option value="Culture Générale">Culture Générale</option>
                  </select>
                </div>

                <div className="w-full md:w-1/2">
                  <label htmlFor="subcategory" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Sous-catégorie</label>
                  <select
                    id="subcategory"
                    value={formData.subCategory}
                    onChange={(e) => setFormData({ ...formData, subCategory: e.target.value })}
                    className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                    required
                  >
                    <option value="">Sélectionner une sous-catégorie...</option>
                    {formData.category === "Sports" && (
                      <>
                        <option value="Football">Football</option>
                        <option value="Basketball">Basketball</option>
                        <option value="Tennis">Tennis</option>
                      </>
                    )}
                    {/* Ajouter d'autres sous-catégories si nécessaire */}
                  </select>
                </div>
              </div>

              <button type="submit" className="block rounded-lg bg-secondary px-8 py-3 text-center text-sm font-semibold text-white outline-none ring-gray-300 transition duration-100 hover:bg-primary focus-visible:ring active:bg-gray-600 md:text-base">Soumettre</button>
              {errorMessage && (
                <div className="flex justify-center">
                  <p className="text-red-500 font-semibold">{errorMessage}</p>
                </div>
              )}
              {successMessage && (
                <div className="flex justify-center">
                  <p className="text-green-500 font-semibold">{successMessage}</p>
                </div>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default QuestionForm;
