import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import LoginTest from './components/loginTest';

function App() {
  return (
    <Router>
      <div className='font-varela bg-accent text-primary h-screen flex flex-col'>
        <header className="App-header">
          <div className="flex justify-center">
            <ul className="nav-bar flex gap-4 list-none">
              <li>
                <Link to="/" className="text-secondary hover:text-gray-800 no-underline">Home</Link> {/* Ajout de styles pour la couleur et l'effet hover, et suppression du soulignement */}
              </li>
              <li>
                <Link to="/login" className="text-secondary hover:text-gray-800 no-underline">Login</Link>
              </li> 
            </ul>
          </div>
        </header>
        <main className="flex-grow">
          <Routes>
            <Route path="/" element={<div><h1>Bienvenue</h1></div>} />
            <Route path="/login" element={<LoginTest />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
