import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

function LoginTest() {
  const navigate = useNavigate();
  const [data, setData] = useState({
    email: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  const loginUser = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/login', {
        email: data.email,
        password: data.password
      });
      if (response.data.error) {
        setErrorMessage(response.data.error);
      } else {
        const userData = response.data;
        localStorage.setItem('user', JSON.stringify(userData)); // Stockez les informations de l'utilisateur dans le stockage local
        setUsername(userData.name);
        setIsLoggedIn(true); // Mettez à jour l'état pour indiquer que l'utilisateur est connecté
        setData({ email: '', password: '' });
        navigate('/dashboard'); // Redirigez l'utilisateur vers le tableau de bord
      }
    } catch (error) {
      console.error('Erreur lors de la connexion:', error);
      setErrorMessage('Erreur lors de la connexion');
    }
  };

  return (
    <div className="bg-white py-6 sm:py-8 lg:py-12 font-varela">
      <div className="mx-auto max-w-screen-2xl px-4 md:px-8">
        {/* Votre contenu de connexion */}
      </div>
    </div>
  );
}

export default LoginTest;
