import React, { useState, useEffect } from 'react';

function Account() {
  const [userName, setUserName] = useState('');
  const [jwtToken, setJwtToken] = useState('');

  useEffect(() => {
    // Récupérer le nom de l'utilisateur depuis le localStorage
    const storedName = localStorage.getItem('name');
    if (storedName) {
      setUserName(storedName);
    }

    // Récupérer le JWT depuis le localStorage avec la clé jwtToken
    const storedToken = localStorage.getItem('jwtToken');
    if (storedToken) {
      setJwtToken(storedToken);
    }
  }, []);

  return (
    <div>
      {userName ? (
        <div>
          <h2>Bienvenue, {userName} !</h2>
          <p>JWT : {jwtToken}</p>
          {/* Afficher d'autres informations si nécessaire */}
        </div>
      ) : (
        <p>Chargement des informations de l'utilisateur...</p>
      )}
    </div>
  );
}

export default Account;
