import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import Sports from './Sports';

const categories = [
  { name: 'Sport', icon: 'üèÄ', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.', link: '/sports', component: <Sports /> },
  { name: 'Cinema', icon: 'üé¨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Histoire', icon: 'üìú', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Sciences', icon: 'üî¨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Arts', icon: 'üé®', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Culture G√©n√©rale', icon: 'üåç', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
];

const Quiz = () => {
  const [hovered, setHovered] = useState(null);

  const scaleVariants = {
    hover: {
      scale: 1.01,
    },
    initial: {
      scale: 1,
    },
  };

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px', display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '20px' }}>
      {categories.map((category, index) => (
        <Link key={index} to={category.link} style={{ textDecoration: 'none' }}>
          <motion.div
            className="grid-item bg-accent"
            onMouseEnter={() => setHovered(index)}
            onMouseLeave={() => setHovered(null)}
            whileHover="hover"
            animate={hovered === index ? 'hover' : 'initial'}
            variants={scaleVariants}
            style={{ backgroundColor: hovered === index ? '#67C6E3' : '#DFF5FF', border: 'none', cursor: 'pointer', padding: '20px', borderRadius: '8px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}
          >
            <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
            <div className="category-info" style={{ textAlign: 'center', marginTop: '10px' }}>
              <span className="category-name" style={{ fontWeight: 'bold', fontSize: '18px' }}>{category.name}</span>
              <div className={`category-description bg-tertiary px-4 py-2 rounded-md transition-opacity ${hovered === index ? 'opacity-100' : 'opacity-0 hidden'}`} style={{ marginTop: '5px' }}>
                {category.description}
              </div>
            </div>
          </motion.div>
        </Link>
      ))}
    </div>
  );
};

export default Quiz;
