import React, { useState } from 'react';
import { motion } from 'framer-motion';
import Sports from './Sports'; // Importez le composant Sports.jsx

const categories = [
  { name: 'Sport', icon: 'ðŸ€', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.', link: '/sports' }, // Ajoutez la propriÃ©tÃ© "link" pour chaque catÃ©gorie
  { name: 'Cinema', icon: 'ðŸŽ¬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Histoire', icon: 'ðŸ“œ', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Sciences', icon: 'ðŸ”¬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Arts', icon: 'ðŸŽ¨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Culture GÃ©nÃ©rale', icon: 'ðŸŒ', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
];

const Quiz = () => {
  const [hovered, setHovered] = useState(null);

  const scaleVariants = {
    hover: {
      scale: 1.01,
    },
    initial: {
      scale: 1,
    },
  };

  return (
    <div className="grid-container" style={{ backgroundColor: 'white' }}>
      {categories.map((category, index) => (
        <a key={index} href={category.link} className="grid-item" style={{ textDecoration: 'none' }}>
          <motion.div
            onMouseEnter={() => setHovered(index)}
            onMouseLeave={() => setHovered(null)}
            whileHover="hover"
            animate={hovered === index ? 'hover' : 'initial'}
            variants={scaleVariants}
            className="category-item" // Ajoutez une classe pour styler les Ã©lÃ©ments individuels
            style={{ backgroundColor: hovered === index ? '#67C6E3' : '#DFF5FF', display: 'flex', justifyContent: 'center', alignItems: 'center' }} // Gardez la couleur de fond et centrez le contenu
          >
            <span role="img" aria-label={category.name} style={{ fontSize: '40px', marginRight: '10px' }}>{category.icon}</span>
            <div className="category-info">
              <span className="category-name">{category.name}</span>
              <div className={`category-description ${hovered === index ? 'opacity-100' : 'opacity-0 hidden'}`}>
                {category.description}
              </div>
            </div>
          </motion.div>
        </a>
      ))}
    </div>
  );
};

export default Quiz;
