// Quiz.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const categories = [
  { name: 'Sport', icon: '🏀', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Cinema', icon: '🎬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Histoire', icon: '📜', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Sciences', icon: '🔬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Arts', icon: '🎨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Culture Générale', icon: '🌍', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
];

const Quiz = () => {
  const [hovered, setHovered] = React.useState(null);

  const scaleVariants = {
    hover: {
      backgroundColor: '#67C6E3',
      scale: 1.01,
    },
    initial: {
      backgroundColor: '#DFF5FF',
      scale: 1,
    },
  };

  const iconStyle = {
    fontSize: '40px',
    marginRight: '10px', // Ajout de la marge à droite pour l'espacement de l'icône
  };

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px' }}>
      {categories.map((category, index) => (
        <motion.div
          key={index}
          className="grid-item bg-accent"
          onMouseEnter={() => setHovered(index)}
          onMouseLeave={() => setHovered(null)}
          whileHover="hover"
          animate={hovered === index ? 'hover' : 'initial'}
          variants={scaleVariants}
          style={{ border: 'none', display: 'flex', alignItems: 'center' }} // Utilisation de flexbox pour aligner l'icône verticalement
        >
          <Link to="/sports" target="_blank" rel="noopener noreferrer" className="no-underline text-black">
            <span role="img" aria-label={category.name} style={iconStyle}>{category.icon}</span> {/* Appliquer le style de l'icône */}
            <div className="category-info">
              <span className="category-name">{category.name}</span>
              <div className={`category-description bg-tertiary px-4 py-2 rounded-md transition-opacity ${hovered === index ? 'opacity-100' : 'opacity-0 hidden'}`}>
                {category.description}
              </div>
            </div>
          </Link>
        </motion.div>
      ))}
    </div>
  );
};

export default Quiz;
