import React from 'react';
import { useState } from 'react';
import { motion } from 'framer-motion';

const categories = [
  { name: 'Sport', icon: 'ðŸ€', description: 'Lorem ipsum dolor' },
  { name: 'Cinema', icon: 'ðŸŽ¬', description: 'Lorem ipsum dolor' },
  { name: 'Histoire', icon: 'ðŸ“œ', description: 'Lorem ipsum dolor' },
  { name: 'Sciences', icon: 'ðŸ”¬', description: 'Lorem ipsum dolor' },
  { name: 'Arts', icon: 'ðŸŽ¨', description: 'Lorem ipsum dolor' },
  { name: 'Culture GÃ©nÃ©rale', icon: 'ðŸŒ', description: 'Lorem ipsum dolor' },
];

const QuizPage = () => {
  const [hovered, setHovered] = useState(null);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  const handleHover = (index, event) => {
    setHovered(index);
    setTooltipPosition({ x: event.clientX, y: event.clientY });
  };

  const handleHoverEnd = () => {
    setHovered(null);
  };

  return (
    <div className="grid-container">
      {categories.map((category, index) => (
        <motion.div
          key={index}
          className="grid-item"
          onMouseEnter={(e) => handleHover(index, e)}
          onMouseLeave={handleHoverEnd}
          whileHover={{ scale: 1.1 }}
        >
          <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
          <div className="category-info">
            <span className="category-name">{category.name}</span>
            {hovered === index && (
              <div className="tooltip" style={{ top: tooltipPosition.y + 20, left: tooltipPosition.x }}>
                {category.description}
              </div>
            )}
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default Quiz;
