// Sport.jsx
import React from 'react';
import { motion } from 'framer-motion';

const Sport = () => {
  const sportCategories = [
    { name: 'Football', icon: '‚öΩ', description: 'Description du football.' },
    { name: 'Basketball', icon: 'üèÄ', description: 'Description du basketball.' },
    { name: 'Tennis', icon: 'üéæ', description: 'Description du tennis.' },
  ];

  return (
    <div style={{ textAlign: 'center' }}> {/* Wrapper div to center elements */}
      <h1 style={{ fontSize: '24px', marginBottom: '20px' }}>Choisissez votre cat√©gorie</h1>
      <div className="grid-container" style={{ backgroundColor: 'white', padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {sportCategories.map((category, index) => (
          <motion.div
            key={index}
            className="grid-item bg-accent"
            style={{ backgroundColor: '#DFF5FF', border: 'none', marginBottom: '20px', textAlign: 'center', padding: '10px', width: '300px' }}
            whileHover={{ scale: 1.05 }}
          >
            <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
            <div className="category-info">
              <span className="category-name">{category.name}</span>
              <div className="category-description bg-tertiary px-4 py-2 rounded-md opacity-0 hidden">
                {category.description}
              </div>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Sport;
