import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function LoginTest({ setIsLoggedIn, isLoggedIn }) {
    const [data, setData] = useState({
        email: '',
        password: ''
    });
    const [errorMessage, setErrorMessage] = useState('');
    const [username, setUsername] = useState('');
    const [jwt, setJwt] = useState('');
    const [score, setScore] = useState(() => {
        const userScore = localStorage.getItem('score');
        return userScore ? parseInt(userScore) : 0;
    });
    const [userRanking, setUserRanking] = useState(null);

    useEffect(() => {
        const loggedInUser = localStorage.getItem('user');
        const token = localStorage.getItem('token');
        if (loggedInUser && token) {
            const userData = JSON.parse(loggedInUser);
            setUsername(userData.name);
            setJwt(token);
            setIsLoggedIn(true);
            fetchUserRanking(token);
        }
    }, [setIsLoggedIn]);

    const loginUser = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8000/login', {
                email: data.email,
                password: data.password
            });
            if (response.data.error) {
                setErrorMessage(response.data.error);
            } else {
                const userData = response.data;
                localStorage.setItem('user', JSON.stringify(userData));
                localStorage.setItem('token', response.data.token);
                setUsername(userData.name);
                setJwt(response.data.token);
                setIsLoggedIn(true);
                setData({ email: '', password: '' });
                fetchScore(response.data.token);
                fetchUserRanking(response.data.token);
            }
        } catch (error) {
            console.error('Erreur lors de la connexion:', error);
            setErrorMessage('Erreur lors de la connexion');
        }
    };

    const fetchScore = async (token) => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            const userScore = response.data.score;
            setScore(userScore);
            localStorage.setItem('score', userScore.toString());
        } catch (error) {
            console.error('Erreur lors de la rÃ©cupÃ©ration du score:', error);
        }
    };

    const fetchUserRanking = async (token) => {
        try {
            const response = await axios.get('http://localhost:8000/user-ranking', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setUserRanking(response.data.ranking);
        } catch (error) {
            console.error('Erreur lors de la rÃ©cupÃ©ration du classement de l\'utilisateur:', error);
        }
    };

    const fetchUserData = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                headers: {
                    Authorization: `Bearer ${jwt}`
                }
            });
            setUsername(response.data.name);
        } catch (error) {
            console.error('Erreur lors de la rÃ©cupÃ©ration des donnÃ©es utilisateur:', error);
        }
    };

    useEffect(() => {
        if (jwt) {
            fetchUserData();
        }
    }, [jwt]);

    const shareOnTwitter = () => {
        const shareUrl = `https://twitter.com/intent/tweet?text=Mon%20score%20sur%20Quoiz%20est%20de%20${score}%20!%20ðŸŽ‰`;
        window.open(shareUrl, '_blank');
    };

    const shareOnFacebook = () => {
        const shareUrl = `https://www.facebook.com/sharer/sharer.php?u=https://example.com&quote=Mon%20score%20sur%20Quoiz%20est%20de%20${score}%20!%20ðŸŽ‰`;
        window.open(shareUrl, '_blank');
    };

    return (
        <div className="register-container">
            <div className="bg-white py-6 sm:py-8 lg:py-12 font-varela">
                <div className="mx-auto max-w-screen-2xl px-4 md:px-8">
                    <h2 className="mb-4 text-center text-2xl font-bold text-gray-800 md:mb-8 lg:text-3xl">Se connecter</h2>

                    <form onSubmit={loginUser} className="mx-auto max-w-lg rounded-lg border">
                        <div className="flex flex-col gap-4 p-4 md:p-8 bg-accent">
                            <div>
                                <label htmlFor="email" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Email</label>
                                <input
                                    type="email"
                                    name="email"
                                    value={data.email}
                                    onChange={(e) => setData({ ...data, email: e.target.value })}
                                    className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                                />
                            </div>

                            <div>
                                <label htmlFor="password" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Password</label>
                                <input
                                    type="password"
                                    name="password"
                                    value={data.password}
                                    onChange={(e) => setData({ ...data, password: e.target.value })}
                                    className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring"
                                />
                            </div>

                            <button type="submit" className="block rounded-lg bg-secondary px-8 py-3 text-center text-sm font-semibold text-white outline-none ring-gray-300 transition duration-100 hover:bg-primary focus-visible:ring active:bg-gray-600 md:text-base">Se connecter</button>
                        </div>
                    </form>

                    {errorMessage && (
                        <div className="flex justify-center">
                            <p className="text-red-500 font-semibold">{errorMessage}</p>
                        </div>
                    )}

                    {!isLoggedIn && (
                        <div className="mt-4 text-sm text-gray-600">
                            Vous n'avez pas de compte ? <Link to="/register" className="font-medium text-indigo-600 hover:text-indigo-500">Register</Link>
                        </div>
                    )}

                    {isLoggedIn && (
                        <div className="flex flex-row justify-center">
                            <button onClick={shareOnTwitter} className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded mr-4">Partager sur Twitter</button>
                            <button onClick={shareOnFacebook} className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded">Partager sur Facebook</button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default LoginTest;
