import React from 'react';

const Sport = () => {
  const sportCategories = [
    { name: 'Football', icon: '‚öΩ', description: 'Description du football.' },
    { name: 'Basketball', icon: 'üèÄ', description: 'Description du basketball.' },
    { name: 'Tennis', icon: 'üéæ', description: 'Description du tennis.' },
  ];

  return (
    <div className="grid-container">
      {sportCategories.map((category, index) => (
        <div key={index} className="grid-item">
          <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
          <div className="category-info">
            <span className="category-name">{category.name}</span>
            <div className="category-description">
              {category.description}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Sport;
