import React, { useState, useEffect } from 'react';

function Football() {
  const initialQuestions = [
    {
      id: 1,
      question: "Quelle équipe a remporté le premier championnat du monde de football en 1930 ?",
      options: ["Uruguay", "Brésil", "Allemagne", "Italie"],
      answer: "Uruguay"
    },
    {
      id: 2,
      question: "Qui a remporté le Ballon d'Or 2020 ?",
      options: ["Lionel Messi", "Robert Lewandowski", "Cristiano Ronaldo", "Virgil van Dijk"],
      answer: "Robert Lewandowski"
    },
    // Ajouter les autres questions ici
  ];

  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [currentScore, setCurrentScore] = useState(0);
  const [totalScore, setTotalScore] = useState(() => {
    const savedTotalScore = localStorage.getItem('footballTotalScore');
    return savedTotalScore ? parseInt(savedTotalScore, 10) : 0;
  });
  const [showScore, setShowScore] = useState(false);
  const [userAnswer, setUserAnswer] = useState(null);

  useEffect(() => {
    const shuffledQuestions = shuffleArray(initialQuestions);
    setQuestions(shuffledQuestions);
  }, []);

  useEffect(() => {
    localStorage.setItem('footballTotalScore', totalScore.toString());
  }, [totalScore]);

  const shuffleArray = (array) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  const handleAnswerButtonClick = (selectedOption) => {
    if (selectedOption === questions[currentQuestion].answer) {
      setCurrentScore(currentScore + 10);
      setUserAnswer('correct');
    } else {
      setUserAnswer('incorrect');
    }
  };

  const handleNextButtonClick = () => {
    setCurrentQuestion(currentQuestion + 1);
    setUserAnswer(null);
  };

  const currentQuestionData = currentQuestion < questions.length ? questions[currentQuestion] : null;

  return (
    <div className="quiz">
      <div className="quiz-content">
        <div className="question-section tertiary-bg">
          <div className="question-count white">
            Question {currentQuestion + 1}
          </div>
          <div className="question-text">
            <h2 className="question">{currentQuestionData ? currentQuestionData.question : ''}</h2>
          </div>
        </div>
        <div className="answer-section">
          {currentQuestionData && currentQuestionData.options.map((option) => (
            <button 
              key={option} 
              onClick={() => handleAnswerButtonClick(option)}
              className={
                userAnswer === 'correct' && option === questions[currentQuestion].answer ? 'correct' :
                userAnswer === 'incorrect' && option === questions[currentQuestion].answer ? 'correct' :
                userAnswer === 'incorrect' && option === questions[currentQuestion].answer ? 'incorrect' :
                ''
              }
              disabled={userAnswer !== null}
            >
              {option}
            </button>
          ))}
        </div>
        {userAnswer && (
          <div className="correct-answer">
            La bonne réponse est : {questions[currentQuestion].answer}
          </div>
        )}
        {userAnswer && currentQuestion < questions.length - 1 && (
          <button onClick={handleNextButtonClick}>Next</button>
        )}
      </div>
      <div className="score-section white-bg">
        Score: <span className="white">{currentScore}</span>
      </div>
      {showScore && (
        <div className="score-final tertiary-bg white">
          Score total: <span className="white">{totalScore}</span>
        </div>
      )}
    </div>
  );
}

export default Football;
