import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom'; // Importez Link depuis react-router-dom
import Sports from './Sports'; // Importez le composant Sports.jsx

const categories = [
  { name: 'Sport', icon: 'üèÄ', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.', component: <Sports /> }, // Ajoutez la propri√©t√© "component" pour chaque cat√©gorie
  { name: 'Cinema', icon: 'üé¨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Histoire', icon: 'üìú', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Sciences', icon: 'üî¨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Arts', icon: 'üé®', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Culture G√©n√©rale', icon: 'üåç', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
];

const Quiz = () => {
  const [hovered, setHovered] = useState(null);

  const scaleVariants = {
    hover: {
      scale: 1.01,
    },
    initial: {
      scale: 1,
    },
  };

  const handleCategoryClick = (index) => {
    if (categories[index].component) {
      // Ouvrez le composant correspondant lorsque vous cliquez sur la cat√©gorie
      return categories[index].component;
    }
  };

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px' }}>
      {categories.map((category, index) => (
        <Link key={index} to="/sports" style={{ textDecoration: 'none' }}>
          <motion.div
            className="grid-item bg-accent"
            onClick={() => handleCategoryClick(index)} // Ajoutez un gestionnaire d'√©v√©nements onClick
            onMouseEnter={() => setHovered(index)}
            onMouseLeave={() => setHovered(null)}
            whileHover="hover"
            animate={hovered === index ? 'hover' : 'initial'}
            variants={scaleVariants}
            style={{ backgroundColor: hovered === index ? '#67C6E3' : '#DFF5FF', border: 'none', cursor: 'pointer' }}
          >
            <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
            <div className="category-info">
              <span className="category-name">{category.name}</span>
              <div className={`category-description bg-tertiary px-4 py-2 rounded-md transition-opacity ${hovered === index ? 'opacity-100' : 'opacity-0 hidden'}`}>
                {category.description}
              </div>
            </div>
          </motion.div>
        </Link>
      ))}
    </div>
  );
};

export default Quiz;
