import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Football() {
  const [question, setQuestion] = useState(null);
  const [quizComplete, setQuizComplete] = useState(false);
  const [score, setScore] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [timeLeft, setTimeLeft] = useState(10);
  const [questionCount, setQuestionCount] = useState(0); // Ajout d'un compteur de questions

  useEffect(() => {
    fetchFootballQuestion();
  }, []);

  useEffect(() => {
    let timer;
    if (timeLeft > 0 && !quizComplete) {
      timer = setTimeout(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      handleAnswerButtonClick(-1); // Sélectionner automatiquement la mauvaise réponse lorsque le temps est écoulé
    }
    return () => clearTimeout(timer);
  }, [timeLeft, quizComplete]);

  const fetchFootballQuestion = async () => {
    try {
      const response = await axios.get('http://localhost:8000/questions/football/random');
      setQuestion(response.data);
      setQuestionCount(questionCount + 1); // Incrémenter le compteur de questions
      setTimeLeft(10); // Réinitialiser le temps à chaque nouvelle question
    } catch (error) {
      console.error('Erreur lors de la récupération de la question de football :', error);
    }
  };

  const handleAnswerButtonClick = (selectedOptionIndex) => {
    const isCorrect = selectedOptionIndex === question.correctOptionIndex;
    let updatedScore = score;
    if (isCorrect) {
      updatedScore += 5; // Ajoute 5 points pour une réponse correcte
      setCorrectAnswers(correctAnswers + 1);
    } else {
      updatedScore -= 1; // Retire 1 point pour une réponse incorrecte
    }
    setScore(updatedScore);
    setQuizComplete(true); // Marquer le quiz comme complet

    setTimeout(() => {
      if (questionCount < 5) { // Arrêter le quiz après 5 questions
        fetchFootballQuestion(); // Charger une nouvelle question après avoir répondu
        setQuizComplete(false); // Réinitialiser le quiz
      } else {
        // Afficher le score final une fois le quiz terminé
        console.log('Score final :', score);
      }
    }, 1000);
  };

  if (!question) {
    return <div>Loading...</div>;
  }

  return (
    <div className="quiz">
      <div className="quiz-content">
        <div className="question-section tertiary-bg">
          <div className="question-count white">
            Question
          </div>
          <div className="question-text">
            <h2 className="question">{question.question}</h2>
          </div>
        </div>
        <div className="answer-section">
          {question.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleAnswerButtonClick(index)}
              className={quizComplete && index === question.correctOptionIndex ? 'correct' : quizComplete && index !== question.correctOptionIndex ? 'incorrect' : ''}
              disabled={quizComplete} // Désactiver les boutons après la réponse
            >
              {option}
            </button>
          ))}
        </div>
        <div className="time-left">
          Temps restant : {timeLeft} secondes
        </div>
        {quizComplete && questionCount === 5 && (
          <h2>Score final : {score}</h2>
        )}
      </div>
    </div>
  );
}

export default Football;
