import React, { useState, useEffect } from 'react';

function Football() {
    const initialQuestions = [
        {
          id: 1,
          question: "Quelle équipe a remporté le premier championnat du monde de football en 1930 ?",
          options: ["Uruguay", "Brésil", "Allemagne", "Italie"],
          answer: "Uruguay"
        },
        {
          id: 2,
          question: "Qui a remporté le Ballon d'Or 2020 ?",
          options: ["Lionel Messi", "Robert Lewandowski", "Cristiano Ronaldo", "Virgil van Dijk"],
          answer: "Robert Lewandowski"
        },
        {
          id: 3,
          question: "Quel joueur a marqué le plus de buts en Coupe du Monde de la FIFA de tous les temps ?",
          options: ["Miroslav Klose", "Pele", "Cristiano Ronaldo", "Lionel Messi"],
          answer: "Miroslav Klose"
        },
        {
          id: 4,
          question: "Dans quel stade s'est déroulée la finale de la Coupe du Monde de la FIFA 2018 ?",
          options: ["Stade Loujniki", "Stade de France", "Wembley Stadium", "Maracanã"],
          answer: "Stade Loujniki"
        },
        {
          id: 5,
          question: "Quelle équipe a remporté le plus de titres de Premier League en Angleterre ?",
          options: ["Manchester United", "Liverpool", "Chelsea", "Arsenal"],
          answer: "Manchester United"
        },
        {
          id: 6,
          question: "Qui est le meilleur buteur de l'histoire de la Ligue des Champions de l'UEFA ?",
          options: ["Lionel Messi", "Cristiano Ronaldo", "Raul", "Robert Lewandowski"],
          answer: "Cristiano Ronaldo"
        },
        {
          id: 7,
          question: "Combien de fois l'Allemagne a-t-elle remporté la Coupe du Monde de la FIFA ?",
          options: ["3 fois", "4 fois", "2 fois", "1 fois"],
          answer: "4 fois"
        },
        {
          id: 8,
          question: "Qui a marqué le 'but de la main de Dieu' lors de la Coupe du Monde de la FIFA 1986 ?",
          options: ["Diego Maradona", "Pele", "Zinedine Zidane", "Lionel Messi"],
          answer: "Diego Maradona"
        },
        {
          id: 9,
          question: "Quel club de football a remporté le plus de Ligue des Champions de l'UEFA ?",
          options: ["Real Madrid", "FC Barcelone", "Bayern Munich", "AC Milan"],
          answer: "Real Madrid"
        },
        {
          id: 10,
          question: "Quel joueur a marqué le plus de buts en une seule saison de Premier League ?",
          options: ["Mohamed Salah", "Thierry Henry", "Cristiano Ronaldo", "Alan Shearer"],
          answer: "Mohamed Salah"
        },
        {
          id: 11,
          question: "Quel est le pays d'origine du joueur de football Lionel Messi ?",
          options: ["Argentine", "Brésil", "Espagne", "Portugal"],
          answer: "Argentine"
        },
        {
          id: 12,
          question: "Qui est le sélectionneur actuel de l'équipe de France de football ?",
          options: ["Didier Deschamps", "Zinédine Zidane", "Thierry Henry", "Arsène Wenger"],
          answer: "Didier Deschamps"
        },
        {
          id: 13,
          question: "Quelle équipe a remporté la Coupe du Monde de la FIFA 2014 ?",
          options: ["Allemagne", "Argentine", "Brésil", "Espagne"],
          answer: "Allemagne"
        },
        {
          id: 14,
          question: "Qui est le seul joueur à avoir remporté le Ballon d'Or avec trois clubs différents ?",
          options: ["Cristiano Ronaldo", "Lionel Messi", "Michel Platini", "Marco van Basten"],
          answer: "Cristiano Ronaldo"
        },
        {
          id: 15,
          question: "Quel joueur a été surnommé 'La Pulga' ?",
          options: ["Lionel Messi", "Diego Maradona", "Neymar", "Pele"],
          answer: "Lionel Messi"
        },
        {
          id: 16,
          question: "Quel pays a remporté la Coupe d'Afrique des Nations 2019 ?",
          options: ["Algérie", "Sénégal", "Nigeria", "Maroc"],
          answer: "Algérie"
        },
        {
          id: 17,
          question: "Qui est le joueur le plus âgé à avoir remporté la Coupe du Monde de la FIFA ?",
          options: ["Dino Zoff", "Roger Milla", "Pele", "Cafu"],
          answer: "Dino Zoff"
        },
        {
          id: 18,
          question: "Quel joueur détient le record du plus grand nombre de buts en une seule édition de la Coupe du Monde ?",
          options: ["Just Fontaine", "Eusébio", "Gerd Müller", "Ronaldo"],
          answer: "Just Fontaine"
        },
        {
          id: 19,
          question: "Quelle est la nationalité du footballeur Eden Hazard ?",
          options: ["Belge", "Française", "Espagnole", "Allemande"],
          answer: "Belge"
        },
        {
          id: 20,
          question: "Dans quelle ville se trouve le siège de la FIFA ?",
          options: ["Zurich", "Genève", "Lausanne", "Bern"],
          answer: "Zurich"
        },
      ];

  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [totalScore, setTotalScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  useEffect(() => {
    const shuffledQuestions = shuffleArray(initialQuestions);
    setQuestions(shuffledQuestions);
  }, []);

  const shuffleArray = (array) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  const handleAnswerButtonClick = (selectedOption) => {
    const isCorrect = selectedOption === questions[currentQuestion].answer;
    if (isCorrect) {
      setScore(score + 10);
    }

    const updatedQuestions = questions.map((question, index) => {
      if (index === currentQuestion) {
        return { ...question, selectedOption, isCorrect };
      } else {
        return question;
      }
    });

    setQuestions(updatedQuestions);

    // Calcule le score total
    const currentTotalScore = updatedQuestions.reduce((acc, question) => {
      return question.isCorrect ? acc + 10 : acc;
    }, 0);
    setTotalScore(currentTotalScore);

    setTimeout(() => {
      const nextQuestion = currentQuestion + 1;
      if (nextQuestion < questions.length) {
        setCurrentQuestion(nextQuestion);
      } else {
        setShowScore(true);
      }
    }, 1000);
  };

  const currentQuestionData = currentQuestion < questions.length ? questions[currentQuestion] : null;

  return (
    <div className="quiz">
      <div className="quiz-content">
        <div className="question-section tertiary-bg">
          <div className="question-count white">
            Question {currentQuestion + 1}
          </div>
          <div className="question-text">
            <h2 className="question">{currentQuestionData ? currentQuestionData.question : ''}</h2>
          </div>
        </div>
        <div className="answer-section">
          {currentQuestionData && currentQuestionData.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleAnswerButtonClick(option)}
              className={currentQuestionData.selectedOption && (option === currentQuestionData.answer ? 'correct' : 'incorrect')}
            >
              {option}
            </button>
          ))}
        </div>
      </div>
      <div className="score-section white-bg">
        Score actuel: <span className="white">{score}</span>
      </div>
      {showScore && (
        <div className="score-final tertiary-bg white">
          Score total: <span className="white">{totalScore}</span>
        </div>
      )}
    </div>
  );
}

export default Football;
