import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import LoginTest from './components/loginTest';
import './App.css';

import logo from './logo.png';

function App() {
  return (
    <Router>
      <div className='font-varela bg-white text-black h-screen flex flex-col'>
        <header className="bg-primary py-4 px-6 flex justify-between items-center">
          <div className="flex items-center">
            <img src={logo} alt="Logo" className="h-8 mr-4" />
            <ul className="nav-bar flex gap-4 list-none">
              <li>
                <Link to="/" className="text-accent font-bold text-xl hover:text-tertiary no-underline">Home</Link> 
              </li>
              <li>
                <Link to="/login" className="text-accent font-bold text-xl hover:text-tertiary no-underline">Login</Link>
              </li> 
            </ul>
          </div>
          {/* Vous pouvez ajouter d'autres éléments à droite si nécessaire */}
        </header>
        <main className="flex-grow">
          <Routes>
            <Route path="/" element={<div><h1>Bienvenue</h1></div>} />
            <Route path="/login" element={<LoginTest />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
