import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import LoginTest from './components/loginTest';
import Register from './components/Register';
import Home from './components/Home';
import Quiz from './components/Quiz';
import Sports from './components/Categories/Sports';
import Football from './components/Categories/Sports/Football';
import QuestionForm from './components/QuestionForm';
import './App.css';
import logo from './logo.png';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    return localStorage.getItem('user') ? true : false;
  });
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.clear();
    setIsLoggedIn(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <Router>
      <div className="font-varela bg-white text-black min-h-screen flex flex-col">
        <header className="bg-primary py-4 px-6 flex justify-between items-center">
          <div className="flex items-center">
            <img src={logo} alt="Logo" className="h-8 mr-4" />
          </div>
          <div className="flex justify-center flex-grow sm:hidden">
            <button onClick={toggleMenu} className="text-white focus:outline-none">
              {isMenuOpen ? (
                <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M13.707 7.293a1 1 0 011.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 111.414-1.414L10 10.586l3.293-3.293a1 1 0 011.414 0z" clipRule="evenodd" />
                </svg>
              ) : (
                <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16m-7 6h7" />
                </svg>
              )}
            </button>
          </div>
          <div className={`sm:flex sm:justify-center sm:flex-grow ${isMenuOpen ? 'block' : 'hidden'}`}>
            <ul className="nav-bar flex flex-col sm:flex-row gap-4 list-none">
              <li className="text-center">
                <Link to="/" className="text-accent font-bold text-xl hover:text-tertiary no-underline" onClick={toggleMenu}>Home</Link>
              </li>
              <li className="text-center">
                <Link to="/quiz" className="text-accent font-bold text-xl hover:text-tertiary no-underline" onClick={toggleMenu}>Quiz</Link>
              </li>
              {isLoggedIn && (
                <li className="text-center">
                  <Link to="/quoizer" className="text-accent font-bold text-xl hover:text-tertiary no-underline" onClick={toggleMenu}>Quoizer</Link>
                </li>
              )}
              <li className="text-center">
                {isLoggedIn ? (
                  <Link to="/login" className="text-primary font-bold text-xl hover:text-tertiary no-underline" onClick={toggleMenu}>Mon compte</Link>
                ) : (
                  <Link to="/login" className="text-primary font-bold text-xl hover:text-tertiary no-underline" onClick={toggleMenu}>Login</Link>
                )}
              </li>
              {!isLoggedIn && (
                <li className="text-center">
                  <Link to="/register" className="text-accent font-bold text-xl hover:text-tertiary no-underline" onClick={toggleMenu}>Register</Link>
                </li>
              )}
            </ul>
          </div>
          <div className="flex items-center sm:hidden">
            {isLoggedIn ? (
              <>
                <Link to="/login" className="text-primary font-bold text-xl hover:text-tertiary no-underline">Mon compte</Link>
                <button onClick={handleLogout} className="text-white font-bold text-xl rounded-full bg-red-600 px-6 py-3 hover:bg-red-700 transition duration-200 inline-block ml-4">Déconnexion</button>
              </>
            ) : (
              <>
                <Link to="/login" className="text-primary font-bold text-xl hover:text-tertiary no-underline">Login</Link>
                <Link to="/register" className="text-accent font-bold text-xl hover:text-tertiary no-underline ml-4">Register</Link>
              </>
            )}
          </div>
        </header>
        <main className="flex-grow flex justify-center">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginTest setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} />} />
            <Route path="/register" element={<Register />} />
            {isLoggedIn && (
              <Route path="/quiz" element={<Quiz />} />
            )}
            <Route path="/categories/sports" element={<Sports />} />
            <Route path="/categories/football" element={<Football />} />
            {!isLoggedIn && (
              <Route path="/quiz" element={<Navigate to="/login" />} />
            )}
            <Route path="/quoizer" element={<QuestionForm />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
