import React from 'react';
import { motion } from 'framer-motion';
import { useHistory } from 'react-router-dom';

const categories = [
  { name: 'Sport', icon: '🏀', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Cinema', icon: '🎬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Histoire', icon: '📜', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Sciences', icon: '🔬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Arts', icon: '🎨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Culture Générale', icon: '🌍', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
];

const Quiz = () => {
  const history = useHistory();

  const handleCategoryClick = (categoryName) => {
    history.push(`/questions/${categoryName}`);
  };

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px' }}>
      {categories.map((category, index) => (
        <motion.div
          key={index}
          className="grid-item bg-accent"
          onClick={() => handleCategoryClick(category.name)}
          style={{ backgroundColor: '#DFF5FF', border: 'none', cursor: 'pointer' }}
        >
          <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
          <div className="category-info">
            <span className="category-name">{category.name}</span>
            <div className="category-description bg-tertiary px-4 py-2 rounded-md opacity-0 hidden">
              {category.description}
            </div>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default Quiz;
