import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const categories = [
  { name: 'Sports', icon: 'üèÄ', disabled: false },
  { name: 'Cinema', icon: 'üé¨', disabled: true },
  { name: 'Histoire', icon: 'üìú', disabled: true },
  { name: 'Sciences', icon: 'üî¨', disabled: true },
  { name: 'Arts', icon: 'üé®', disabled: true },
  { name: 'Culture G√©n√©rale', icon: 'üåç', disabled: true },
];

const Quiz = () => {
  const [hovered, setHovered] = useState(null);
  const [currentCategory, setCurrentCategory] = useState(0);

  const scaleVariants = {
    hover: {
      scale: 1.01,
      transition: {
        duration: 0.2
      }
    },
    initial: {
      scale: 1,
    },
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentCategory(prevCategory => (prevCategory === categories.length - 1 ? 0 : prevCategory + 1));
    }, 1000); // Changez cette valeur pour ajuster la vitesse de changement des cat√©gories

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px' }}>
      <motion.div
        className="grid-item bg-accent"
        onMouseEnter={() => setHovered(currentCategory)}
        onMouseLeave={() => setHovered(null)}
        whileHover={categories[currentCategory].disabled ? null : "hover"}
        animate={hovered === currentCategory && !categories[currentCategory].disabled ? 'hover' : 'initial'}
        variants={scaleVariants}
        style={{ 
          backgroundColor: categories[currentCategory].disabled ? '#D3D3D3' : (hovered === currentCategory ? '#67C6E3' : '#DFF5FF'), 
          border: 'none', 
          textAlign: 'center',
          pointerEvents: categories[currentCategory].disabled ? 'none' : 'auto' // D√©sactive les √©v√©nements de pointage si la cat√©gorie est d√©sactiv√©e
        }}
      >
        <Link to={`/categories/${categories[currentCategory].name.toLowerCase()}`} className="no-underline text-black">
          <span role="img" aria-label={categories[currentCategory].name} style={{ fontSize: '40px', display: 'inline-block' }}>{categories[currentCategory].icon}</span>
          <div className="category-info" style={{ display: 'inline-block', marginLeft: '10px' }}>
            <span className="category-name">{categories[currentCategory].name}</span>
            {categories[currentCategory].disabled && (
              <p style={{ margin: '5px auto 0', color: '#333', fontSize: '14px', textAlign: 'center' }}>Bient√¥t !</p>
            )}
          </div>
        </Link>
      </motion.div>
    </div>
  );
};

export default Quiz;
