import React, { useState, useEffect } from 'react';

const questions = [
  {
    question: 'Quel est le sport national du Japon ?',
    answers: ['A. Sumo', 'B. Baseball', 'C. Judo', 'D. Football'],
    correctAnswer: 'A. Sumo',
  },
  {
    question: 'Quelle est la longueur du Marathon ?',
    answers: ['A. 26 miles', 'B. 13 miles', 'C. 20 miles', 'D. 100 miles'],
    correctAnswer: 'A. 26 miles',
  },
  {
    question: 'Dans quel sport utilise-t-on une raquette pour frapper un volant ?',
    answers: ['A. Tennis', 'B. Badminton', 'C. Squash', 'D. Golf'],
    correctAnswer: 'B. Badminton',
  }, // Ajoutez la virgule ici
  {
    question: "Quelle est la durée d'un match de basket-ball de la NBA ?",
    answers: ['A. 40 minutes', 'B. 48 minutes', 'C. 60 minutes', 'D. 90 minutes'],
    correctAnswer: 'B. 48 minutes',
  },
  {
    question: 'Qui est souvent considéré comme le plus grand joueur de football de tous les temps ?',
    answers: ['A. Lionel Messi', 'B. Cristiano Ronaldo', 'C. Diego Maradona', 'D. Pelé'],
    correctAnswer: 'D. Pelé',
  },
  {
    question: 'Quelle est la taille réglementaire d\'un terrain de football ?',
    answers: ['A. 90 m x 45 m', 'B. 100 m x 50 m', 'C. 110 m x 60 m', 'D. 120 m x 70 m'],
    correctAnswer: 'A. 90 m x 45 m',
  },
  {
    question: 'Quel est le record du monde du saut à la perche en plein air ?',
    answers: ['A. 6.12 m', 'B. 6.18 m', 'C. 6.45 m', 'D. 6.60 m'],
    correctAnswer: 'B. 6.18 m',
  },
  {
    question: 'Dans quel sport utilise-t-on une épée ?',
    answers: ['A. Escrime', 'B. Boxe', 'C. Karaté', 'D. Judo'],
    correctAnswer: 'A. Escrime',
  },
  {
    question: 'Quel est le record du monde du 100 mètres masculin ?',
    answers: ['A. 9.58 secondes', 'B. 9.63 secondes', 'C. 9.69 secondes', 'D. 9.72 secondes'],
    correctAnswer: 'A. 9.58 secondes',
  },
  {
    question: 'Qui a remporté le plus de titres du Grand Chelem en tennis masculin ?',
    answers: ['A. Roger Federer', 'B. Rafael Nadal', 'C. Novak Djokovic', 'D. Pete Sampras'],
    correctAnswer: 'A. Roger Federer',
  },
];

const Sports = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [timer, setTimer] = useState(30);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer(prevTimer => prevTimer - 1);
    }, 1000);

    if (timer === 0) {
      handleNextQuestion();
    }

    return () => clearInterval(interval);
  }, [timer]);

  const handleAnswerClick = (answer) => {
    const currentQuestion = questions[currentQuestionIndex];
    if (answer === currentQuestion.correctAnswer) {
      setScore(prevScore => prevScore + 10 + timer); // Ajoutez le temps restant au score
    }
    handleNextQuestion();
  };

  const handleNextQuestion = () => {
    setTimer(30);
    setCurrentQuestionIndex(prevIndex => prevIndex + 1);
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-container">
      <div className="question" style={{ backgroundColor: '#67C6E3' }}>
        <div className="score" style={{ color: 'white', marginBottom: '10px' }}>Score: {score}</div>
        <div className="question-text" style={{ fontWeight: 'bold' }}>{currentQuestion.question}</div>
        <div className="timer">Temps restant: {timer} secondes</div>
      </div>
      <div className="answers">
        {currentQuestion.answers.map((answer, index) => (
          <div key={index} className="answer" onClick={() => handleAnswerClick(answer)} style={{ backgroundColor: '#DFF5FF', cursor: 'pointer' }}>
            {answer}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sports;
