import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const categories = [
  { name: 'Sports', icon: 'üèÄ' },
  { name: 'Cinema', icon: 'üé¨' },
  { name: 'Histoire', icon: 'üìú' },
  { name: 'Sciences', icon: 'üî¨' },
  { name: 'Arts', icon: 'üé®' },
  { name: 'Culture G√©n√©rale', icon: 'üåç' },
];

const Quiz = () => {
  const [hovered, setHovered] = useState(null);

  const scaleVariants = {
    hover: {
      scale: 1.01,
      transition: {
        duration: 0.2
      }
    },
    initial: {
      scale: 1,
    },
  };

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px' }}>
      {categories.map((category, index) => (
        <motion.div
          key={index}
          className="grid-item bg-accent"
          onMouseEnter={() => setHovered(index)}
          onMouseLeave={() => setHovered(null)}
          whileHover="hover"
          animate={hovered === index ? 'hover' : 'initial'}
          variants={scaleVariants}
          style={{
            backgroundColor: hovered === index ? '#67C6E3' : '#DFF5FF',
            border: 'none',
            textAlign: 'center',
            pointerEvents: 'auto' // Activer les √©v√©nements de souris par d√©faut
          }}
        >
          <Link to={`/categories/${category.name.toLowerCase()}`} className="no-underline text-black">
            <span role="img" aria-label={category.name} style={{ fontSize: '40px', display: 'inline-block' }}>{category.icon}</span>
            <div className="category-info" style={{ display: 'inline-block', marginLeft: '10px' }}>
              <span className="category-name">{category.name}</span>
            </div>
          </Link>
          {index !== 0 && ( // Afficher le texte "Bient√¥t !" sauf pour la premi√®re cat√©gorie
            <span style={{ color: 'red', fontSize: '14px', marginLeft: '5px' }}>Bient√¥t !</span>
          )}
        </motion.div>
      ))}
    </div>
  );
};

export default Quiz;
