import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';

export default function Register() {
  const navigate = useNavigate();
  const [data, setData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/register', {
        username: data.username,
        email: data.email,
        password: data.password,
      });

      if (response.data.error) {
        toast.error(response.data.error);
      } else {
        setData({ username: '', email: '', password: '' });
        toast.success('Inscription réussie !');
        navigate('/login');
      }
    } catch (error) {
      console.error('Erreur lors de l\'inscription:', error);
    }
  };

  return (
    <div className="register-container">
      <div className="bg-white py-6 sm:py-8 lg:py-12 font-varela">
        <div className="mx-auto max-w-screen-2xl px-4 md:px-8">
          <h2 className="mb-4 text-center text-2xl font-bold text-gray-800 md:mb-8 lg:text-3xl">Register</h2>

          <form className="mx-auto max-w-lg rounded-lg border" onSubmit={handleSubmit}>
            <div className="flex flex-col gap-4 p-4 md:p-8 bg-accent">
              <div className="w-full">
                <label htmlFor="username" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Username</label>
                <input name="username" value={data.username} onChange={handleChange} className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring" />
              </div>

              <div className="w-full">
                <label htmlFor="email" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Email</label>
                <input name="email" type="email" value={data.email} onChange={handleChange} className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring" />
              </div>

              <div className="w-full">
                <label htmlFor="password" className="mb-2 inline-block text-sm text-gray-800 sm:text-base">Password</label>
                <input name="password" type="password" value={data.password} onChange={handleChange} className="w-full rounded border bg-gray-50 px-3 py-2 text-gray-800 outline-none ring-indigo-300 transition duration-100 focus:ring" />
              </div>

              <button type="submit" className="block rounded-lg bg-secondary px-8 py-3 text-center text-sm font-semibold text-white outline-none ring-gray-300 transition duration-100 hover:bg-primary focus-visible:ring active:bg-gray-600 md:text-base">Register</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
