import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function LoginTest({ setIsLoggedIn, isLoggedIn }) {
  const [data, setData] = useState({
    email: '',
    password: '',
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [username, setUsername] = useState('');
  const [jwt, setJwt] = useState('');
  const [score, setScore] = useState(0); // State variable to store user's score

  useEffect(() => {
    const loggedInUser = localStorage.getItem('user');
    const token = localStorage.getItem('token');
    if (loggedInUser && token) {
      const userData = JSON.parse(loggedInUser);
      setUsername(userData.name);
      setJwt(token);
      setIsLoggedIn(true);
      // Fetch user score when logged in
      fetchUserScore();
    }
  }, [setIsLoggedIn]);

  const loginUser = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/login', {
        email: data.email,
        password: data.password,
      });
      if (response.data.error) {
        setErrorMessage(response.data.error);
      } else {
        const userData = response.data;
        localStorage.setItem('user', JSON.stringify(userData));
        localStorage.setItem('token', response.data.token);
        setUsername(userData.name);
        setJwt(response.data.token);
        setIsLoggedIn(true);
        setData({ email: '', password: '' });
        fetchUserScore(); // Fetch score after successful login
      }
    } catch (error) {
      console.error('Erreur lors de la connexion:', error);
      setErrorMessage('Erreur lors de la connexion');
    }
  };

  const fetchUserScore = async () => {
    try {
      const response = await axios.get('http://localhost:8000/user/score', {
        headers: {
          Authorization: `Bearer ${jwt}`,
        },
      });
      setScore(response.data.score); // Update score state with retrieved data
    } catch (error) {
      console.error('Erreur lors de la récupération du score de l\'utilisateur:', error);
    }
  };

  useEffect(() => {
    if (jwt) {
      fetchUserData();
    }
  }, [jwt]);

  return (
    <div className="bg-white py-6 sm:py-8 lg:py-12 font-varela">
      <div className="mx-auto max-w-screen-2xl px-4 md:px-8">
        <h2 className="mb-4 text-center text-2xl font-bold text-gray-800 md:mb-8 lg:text-3xl">Login</h2>

        {isLoggedIn ? (
          <div className="text-center">
            <p className="mb-4 text-xl">Bienvenue, {username}!</p>
            <p className="mb-2 text-sm">Votre score actuel : {score}</p> {/* Display score */}
            <p className="text-sm text-gray-500">Vous êtes connecté avec succès.</p>
          </div>
        ) : (
          <form className="mx-auto max-w-lg rounded-lg border" onSubmit={loginUser}>
            <div className="flex flex-col gap-4 p-4 md:p-8 bg-accent">
              {/* ... login form fields ... */}
            </div>
            {/* ... rest of the login form code ... */}
          </form>
        )}

        {errorMessage && (
          <div className="flex items-center justify-center bg-gray-100 p-4 mt-4">
            <p className="text-center text-sm text-red-500">{errorMessage}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default LoginTest;
