// App.jsx
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import LoginTest from './components/loginTest';
import Register from './components/Register';
import Home from './components/Home';
import Quiz from './components/Quiz';
import Sports from './components/Sports';
import './App.css';

import logo from './logo.png';

function App() {
  const [showDropdown, setShowDropdown] = useState(false); // État pour gérer l'affichage du menu déroulant

  const categories = [
    'Sport',
    'Cinema',
    'Histoire',
    'Sciences',
    'Arts',
    'Culture Générale',
  ];

  return (
    <Router>
      <div className='font-varela bg-white text-black h-screen flex flex-col'>
        <header className="bg-primary py-4 px-6 flex justify-between items-center">
          <div className="flex items-center">
            <img src={logo} alt="Logo" className="h-8 mr-4" />
          </div>
          <div className="flex justify-center flex-grow">
            <ul className="nav-bar flex gap-4 list-none">
              <li className="text-center">
                <Link to="/" className="text-accent font-bold text-xl hover:text-tertiary no-underline">Home</Link>
              </li>
              <li className="text-center" onMouseEnter={() => setShowDropdown(true)} onMouseLeave={() => setShowDropdown(false)}> {/* Gérer le hover pour afficher/masquer le menu déroulant */}
                <span className="text-accent font-bold text-xl cursor-pointer">Quiz</span>
                {showDropdown && ( /* Afficher le menu déroulant si showDropdown est vrai */
                  <ul className="dropdown-menu absolute bg-white shadow-lg rounded-md py-2 mt-2">
                    {categories.map((category, index) => (
                      <li key={index} className="px-4 py-2">
                        <Link to={`/quiz/${category.toLowerCase()}`} className="text-black hover:text-tertiary">{category}</Link>
                      </li>
                    ))}
                  </ul>
                )}
              </li>
            </ul>
          </div>
          <div className="flex items-center">
            <ul className="nav-bar flex gap-4 list-none">
              <li>
                <Link to="/login" className="text-accent font-bold text-xl rounded-full bg-secondary px-6 py-3 hover:bg-tertiary transition duration-200 inline-block">
                  Login
                </Link>
              </li>
              <li>
                <Link to="/register" className="text-accent font-bold text-xl rounded-full bg-secondary px-6 py-3 hover:bg-tertiary transition duration-200 inline-block">
                  Register
                </Link>
              </li>
            </ul>
          </div>
        </header>
        <main className="flex-grow flex justify-center">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginTest />} />
            <Route path="/register" element={<Register />} />
            <Route path="/quiz" element={<Quiz />} /> {/* Route pour le composant Quiz */}
            <Route path="/quiz/:category" element={<Sports />} /> {/* Route pour le composant Sport avec le paramètre de catégorie */}
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
