import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import LoginTest from './components/loginTest';
import Register from './components/Register';
import Home from './components/Home';
import Quiz from './components/Quiz';
import Sports from './components/Categories/Sports';
import Football from './components/Categories/Sports/Football';
import QuestionForm from './components/QuestionForm';
import './App.css';
import logo from './logo.png';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    return localStorage.getItem('user') ? true : false;
  });
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.clear();
    setIsLoggedIn(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <Router>
      <div className="font-varela bg-white text-black min-h-screen flex flex-col">
        <header className="bg-primary py-4 px-6 flex justify-between items-center">
          <div>
            <img src={logo} alt="Logo" className="h-8 mr-4" />
          </div>
          <button onClick={toggleMenu} className="text-white focus:outline-none">
            {isMenuOpen ? (
              <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M3 4h14a1 1 0 011 1v2a1 1 0 01-1 1H3a1 1 0 01-1-1V5a1 1 0 011-1zm0 8h14a1 1 0 011 1v2a1 1 0 01-1 1H3a1 1 0 01-1-1v-2a1 1 0 011-1zm0-8a1 1 0 011-1h14a1 1 0 011 1v2a1 1 0 01-1 1H3a1 1 0 01-1-1V4z" clipRule="evenodd" />
              </svg>
            ) : (
              <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 15.586l-4-4V7h8v4.586l-4 4zM4 5h12a1 1 0 010 2H4a1 1 0 010-2z" clipRule="evenodd" />
              </svg>
            )}
          </button>
          <div className={`sm:flex sm:justify-center sm:flex-grow ${isMenuOpen ? 'block' : 'hidden'}`}>
            <ul className="nav-bar flex flex-col sm:flex-row gap-4 list-none">
              <li>
                <Link to="/" className="text-accent font-bold hover:text-tertiary no-underline" onClick={toggleMenu}>Home</Link>
              </li>
              <li>
                <Link to="/quiz" className="text-accent font-bold hover:text-tertiary no-underline" onClick={toggleMenu}>Quiz</Link>
              </li>
              {isLoggedIn && (
                <li>
                  <Link to="/quoizer" className="text-accent font-bold hover:text-tertiary no-underline" onClick={toggleMenu}>Quoizer</Link>
                </li>
              )}
            </ul>
          </div>
          <div>
            <ul className="nav-bar flex gap-4 list-none">
              {isLoggedIn ? (
                <>
                  <li>
                    <Link to="/login" className="text-primary font-bold rounded-full bg-white px-6 py-3 hover:bg-tertiary transition duration-200 inline-block" onClick={toggleMenu}>
                      Mon compte
                    </Link>
                  </li>
                  <li>
                    <button onClick={handleLogout} className="text-white font-bold rounded-full bg-red-600 px-6 py-3 hover:bg-red-700 transition duration-200 inline-block">
                      DÃ©connexion
                    </button>
                  </li>
                </>
              ) : (
                <li>
                  <Link to="/login" className="text-primary font-bold rounded-full bg-white px-6 py-3 hover:bg-tertiary transition duration-200 inline-block" onClick={toggleMenu}>
                    Login
                  </Link>
                </li>
              )}
              {!isLoggedIn && (
                <li>
                  <Link to="/register" className="text-accent font-bold rounded-full bg-secondary px-6 py-3 hover:bg-tertiary transition duration-200 inline-block" onClick={toggleMenu}>
                    Register
                  </Link>
                </li>
              )}
            </ul>
          </div>
        </header>
        <main className="flex-grow flex justify-center">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginTest setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} />} />
            <Route path="/register" element={<Register />} />
            {isLoggedIn && (
              <Route path="/quiz" element={<Quiz />} />
            )}
            <Route path="/categories/sports" element={<Sports />} />
            <Route path="/categories/football" element={<Football />} />
            {!isLoggedIn && (
              <Route path="/quiz" element={<Navigate to="/login" />} />
            )}
            {isLoggedIn && (
              <Route path="/quoizer" element={<QuestionForm />} />
            )}
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
