import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const categories = [
  { name: 'Sports', icon: 'üèÄ', disabled: false },
  { name: 'Cinema', icon: 'üé¨', disabled: true },
  { name: 'Histoire', icon: 'üìú', disabled: true },
  { name: 'Sciences', icon: 'üî¨', disabled: true },
  { name: 'Arts', icon: 'üé®', disabled: true },
  { name: 'Culture G√©n√©rale', icon: 'üåç', disabled: true },
];

const Quiz = () => {
  const [currentCategory, setCurrentCategory] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentCategory(prevCategory => (prevCategory === categories.length - 1 ? 0 : prevCategory + 1));
    }, 3000); // Changez cette valeur pour ajuster la vitesse de d√©filement des cat√©gories

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px' }}>
      <div className="grid-item bg-accent" style={{ border: 'none', textAlign: 'center' }}>
        <motion.div
          animate={{ y: -currentCategory * 100 }} // Ajustez la hauteur du d√©filement ici
          transition={{ type: "tween", duration: 1, ease: "linear" }} // Ajustez la dur√©e de l'animation ici
        >
          {categories.map((category, index) => (
            <Link
              key={index}
              to={`/categories/${category.name.toLowerCase()}`}
              className="no-underline text-black"
              style={{ display: 'block', marginBottom: '20px' }}
            >
              <span role="img" aria-label={category.name} style={{ fontSize: '40px', marginRight: '10px' }}>{category.icon}</span>
              <span className="category-name">{category.name}</span>
              {category.disabled && (
                <p style={{ margin: '5px auto 0', color: '#333', fontSize: '14px', textAlign: 'center' }}>Bient√¥t !</p>
              )}
            </Link>
          ))}
        </motion.div>
      </div>
    </div>
  );
};

export default Quiz;
