import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const categories = [
  { name: 'Sport', icon: '🏀', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Cinema', icon: '🎬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Histoire', icon: '📜', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Sciences', icon: '🔬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Arts', icon: '🎨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Culture Générale', icon: '🌍', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
];

const Quiz = () => {
  const [hovered, setHovered] = useState(null);
  const [totalScore, setTotalScore] = useState(0); // Nouvel état pour stocker le score total

  const scaleVariants = {
    hover: {
      scale: 1.01,
    },
    initial: {
      scale: 1,
    },
  };

  useEffect(() => {
    const storedTotalScore = localStorage.getItem('quizTotalScore');
    if (storedTotalScore) {
      setTotalScore(parseInt(storedTotalScore));
    }
  }, []); // Utilisation d'un effet de chargement pour récupérer le score total depuis le stockage local

  useEffect(() => {
    localStorage.setItem('quizTotalScore', totalScore.toString());
  }, [totalScore]); // Utilisation d'un effet pour sauvegarder le score total dans le stockage local à chaque changement

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px' }}>
      {categories.map((category, index) => (
        <motion.div
          key={index}
          className="grid-item bg-accent"
          onMouseEnter={() => setHovered(index)}
          onMouseLeave={() => setHovered(null)}
          whileHover="hover"
          animate={hovered === index ? 'hover' : 'initial'}
          variants={scaleVariants}
          style={{ backgroundColor: hovered === index ? '#67C6E3' : '#DFF5FF', border: 'none', position: 'relative' }}
        >
          <Link to={`/categories/${category.name.toLowerCase()}`} className="no-underline text-black" style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)' }}>
            <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
            <div className="category-info">
              <span className="category-name">{category.name}</span>
              <div className={`category-description bg-tertiary px-4 py-2 rounded-md transition-opacity ${hovered === index ? 'opacity-100' : 'opacity-0 hidden'}`}>
                {category.description}
              </div>
            </div>
          </Link>
        </motion.div>
      ))}
    </div>
  );
};

export default Quiz;
