// Quiz.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const categories = [
  { name: 'Sport', icon: 'ðŸ€', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Cinema', icon: 'ðŸŽ¬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Histoire', icon: 'ðŸ“œ', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Sciences', icon: 'ðŸ”¬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Arts', icon: 'ðŸŽ¨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Culture GÃ©nÃ©rale', icon: 'ðŸŒ', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
];

const Quiz = () => {
  const [hovered, setHovered] = React.useState(null);

  const scaleVariants = {
    hover: {
      scale: 1.01,
    },
    initial: {
      scale: 1,
    },
  };

  return (
    <div className="grid-container" style={{ backgroundColor: 'white', marginTop: '20px', padding: '20px' }}>
      {categories.map((category, index) => (
        <motion.div
          key={index}
          className="grid-item bg-accent"
          onMouseEnter={() => setHovered(index)}
          onMouseLeave={() => setHovered(null)}
          whileHover="hover"
          animate={hovered === index ? 'hover' : 'initial'}
          variants={scaleVariants}
          style={{ backgroundColor: hovered === index ? '#67C6E3' : '#DFF5FF', border: 'none' }}
        >
          <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
          <div className="category-info">
            <span className="category-name">{category.name}</span>
            <div className={`category-description bg-tertiary px-4 py-2 rounded-md transition-opacity ${hovered === index ? 'opacity-100' : 'opacity-0 hidden'}`}>
              {category.description}
            </div>
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default Quiz;
