import React, { useState, useEffect } from 'react';

function Football() {
  const questions = [
    {
      id: 1,
      question: "Quelle équipe a remporté le premier championnat du monde de football en 1930 ?",
      options: ["Uruguay", "Brésil", "Allemagne", "Italie"],
      answer: "Uruguay"
    },
    {
      id: 2,
      question: "Qui a remporté le Ballon d'Or 2020 ?",
      options: ["Lionel Messi", "Robert Lewandowski", "Cristiano Ronaldo", "Virgil van Dijk"],
      answer: "Robert Lewandowski"
    },
    {
      id: 3,
      question: "Quel joueur a marqué le plus de buts en Coupe du Monde de la FIFA de tous les temps ?",
      options: ["Miroslav Klose", "Pele", "Cristiano Ronaldo", "Lionel Messi"],
      answer: "Miroslav Klose"
    },
    {
      id: 4,
      question: "Dans quel stade s'est déroulée la finale de la Coupe du Monde de la FIFA 2018 ?",
      options: ["Stade Loujniki", "Stade de France", "Wembley Stadium", "Maracanã"],
      answer: "Stade Loujniki"
    },
    {
      id: 5,
      question: "Quelle équipe a remporté le plus de titres de Premier League en Angleterre ?",
      options: ["Manchester United", "Liverpool", "Chelsea", "Arsenal"],
      answer: "Manchester United"
    },
    {
      id: 6,
      question: "Qui est le meilleur buteur de l'histoire de la Ligue des Champions de l'UEFA ?",
      options: ["Lionel Messi", "Cristiano Ronaldo", "Raul", "Robert Lewandowski"],
      answer: "Cristiano Ronaldo"
    },
    {
      id: 7,
      question: "Combien de fois l'Allemagne a-t-elle remporté la Coupe du Monde de la FIFA ?",
      options: ["3 fois", "4 fois", "2 fois", "1 fois"],
      answer: "4 fois"
    },
    {
      id: 8,
      question: "Qui a marqué le 'but de la main de Dieu' lors de la Coupe du Monde de la FIFA 1986 ?",
      options: ["Diego Maradona", "Pele", "Zinedine Zidane", "Lionel Messi"],
      answer: "Diego Maradona"
    },
    {
      id: 9,
      question: "Quel club de football a remporté le plus de Ligue des Champions de l'UEFA ?",
      options: ["Real Madrid", "FC Barcelone", "Bayern Munich", "AC Milan"],
      answer: "Real Madrid"
    },
    {
      id: 10,
      question: "Quel joueur a marqué le plus de buts en une seule saison de Premier League ?",
      options: ["Mohamed Salah", "Thierry Henry", "Cristiano Ronaldo", "Alan Shearer"],
      answer: "Mohamed Salah"
    }
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(() => {
    // Chargement du score depuis le stockage local au chargement initial de la page
    const savedScore = localStorage.getItem('footballScore');
    return savedScore ? parseInt(savedScore, 10) : 0;
  });
  const [showScore, setShowScore] = useState(false);

  useEffect(() => {
    // Sauvegarde du score dans le stockage local à chaque changement
    localStorage.setItem('footballScore', score.toString());
  }, [score]);

  const handleAnswerButtonClick = (selectedOption) => {
    if (selectedOption === questions[currentQuestion].answer) {
      setScore(score + 10); // Ajoute 10 points pour une bonne réponse
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  const currentQuestionData = currentQuestion < questions.length ? questions[currentQuestion] : null;

  return (
    <div className="quiz">
      <div className="score-section">
        Score: {score}
      </div>
      {showScore ? (
        <div className="score-section">
          <p>Score final: {score}</p>
        </div>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>Question {currentQuestion + 1}</span>/{questions.length}
            </div>
            <div className="question-text">{currentQuestionData ? currentQuestionData.question : ''}</div>
          </div>
          <div className="answer-section">
            {currentQuestionData && currentQuestionData.options.map((option) => (
              <button key={option} onClick={() => handleAnswerButtonClick(option)}>{option}</button>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default Football;
