import React from 'react';
import { useState } from 'react';
import { motion } from 'framer-motion';

const categories = [
  { name: 'Sport', icon: 'ðŸ€', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Cinema', icon: 'ðŸŽ¬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Histoire', icon: 'ðŸ“œ', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Sciences', icon: 'ðŸ”¬', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Arts', icon: 'ðŸŽ¨', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
  { name: 'Culture GÃ©nÃ©rale', icon: 'ðŸŒ', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
];

const Quiz = () => {
  const [hovered, setHovered] = useState(null);

  const handleHover = (index) => {
    setHovered(index);
  };

  const handleHoverEnd = () => {
    setHovered(null);
  };

  return (
    <div className="grid-container">
      {categories.map((category, index) => (
        <motion.div
          key={index}
          className="grid-item"
          onHoverStart={() => handleHover(index)}
          onHoverEnd={handleHoverEnd}
          whileHover={{ scale: 1.1 }}
        >
          <span role="img" aria-label={category.name} style={{ fontSize: '40px' }}>{category.icon}</span>
          <div className="category-info">
            <span className="category-name">{category.name}</span>
            {hovered === index && (
              <div className="tooltip">
                {category.description}
              </div>
            )}
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default QuizPage;
